/**
 * code for two ball auto using timings
 * fairly reliable despite using timings
 * INITIAL FRAMEOUT - NEEDS TUNING!
 * 
 * @author WAVE Robotics 2826
 */
TaxiTwoBallAuto::TaxiTwoBallAuto(Punch* punch, Drive* drive, Intake* intake, Transport* transport) {

    SetName("TaxiTwoBallAuto");
    
    AddCommands(

                   
        frc2::ParallelCommandGroup
        (
            TransportCommand(intake, transport),
            // DeployIntakeCommand(units::second_t(2), intake), 
            // score ball 1                   
            FrontPunchCommand(punch),         

            frc2::SequentialCommandGroup
            ( 
                WaitCommand1(units::second_t(1.5)),
                // forward ball 2
                DriveHeadingCommand(-0.75, units::time::second_t(0.75), drive),
                DriveHeadingCommand(-0.15, units::time::second_t(0.3), drive),
                WaitCommand1(units::time::second_t(1.5)),
                // backwards to hub
                DriveHeadingCommand(0.75, units::time::second_t(0.75), drive),
                DriveHeadingCommand(0.15, units::time::second_t(0.3), drive),
                WaitCommand1(units::time::second_t(2.5)),
                // score ball 2
                FrontPunchCommand(punch),
                WaitCommand1(units::time::second_t(0.5)),  
                // taxi
                DriveHeadingCommand(-1.0, units::time::second_t(0.75), drive),
                DriveHeadingCommand(-0.15, units::time::second_t(0.3), drive),                
                DriveTurnCommand(0.5, 90.0f, drive)    // turn in place
            )          
        )
    );
}

bool TaxiTwoBallAuto::RunsWhenDisabled() const {
    return false;
}